import React, { useRef, useEffect, useState } from 'react';
import './styles.css';

const CANVAS_WIDTH = 1000;
const CANVAS_HEIGHT = 600;
const PADDLE_WIDTH = CANVAS_WIDTH / 40;
const PADDLE_HEIGHT = CANVAS_HEIGHT / 5;
const PADDLE_SPEED = 18;
const BALL_SIZE = (30 * CANVAS_WIDTH) / (4 * CANVAS_HEIGHT);;
const BORDER_PADDING = (30 * CANVAS_WIDTH) / (2 * CANVAS_HEIGHT);
const BORDER_PADDING_SIDE = BORDER_PADDING / 2;
const BORDER_THICKNESS = CANVAS_WIDTH / 100;
const BALL_SPEED_X = 4;
const BALL_SPEED_Y = 4;
const TICKS_PER_SEC = 60;

const Game: React.FC = () => {
    const canvasRef = useRef<HTMLCanvasElement>(null);
    const animationFrameId = useRef<number | null>(null);

    const [leftPaddle, setLeftPaddle] = useState({
        x: BORDER_PADDING,
        y: (CANVAS_HEIGHT - PADDLE_HEIGHT) / 2,
        width: PADDLE_WIDTH,
        height: PADDLE_HEIGHT,
        dy: PADDLE_SPEED
    });

    const [rightPaddle, setRightPaddle] = useState({
        x: CANVAS_WIDTH - PADDLE_WIDTH - BORDER_PADDING,
        y: (CANVAS_HEIGHT - PADDLE_HEIGHT) / 2,
        width: PADDLE_WIDTH,
        height: PADDLE_HEIGHT,
        dy: PADDLE_SPEED
    });

    const [ball, setBall] = useState({
        x: CANVAS_WIDTH / 2,
        y: CANVAS_HEIGHT / 2,
        size: BALL_SIZE,
        vx: BALL_SPEED_X,
        vy: BALL_SPEED_Y
    });

    const updateBallPosition = () => {
        setBall(prevState => {
            let newX = prevState.x + prevState.vx;
            let newY = prevState.y + prevState.vy;

            // Bounce off top and bottom borders
            if (newY - prevState.size < BORDER_THICKNESS || newY + prevState.size > CANVAS_HEIGHT - BORDER_THICKNESS) {
                return { ...prevState, vy: -prevState.vy, x: newX, y: newY };
            }

            // Bounce off left paddle
            if (
                newX - prevState.size < leftPaddle.x + leftPaddle.width &&
                newY + prevState.size > leftPaddle.y &&
                newY - prevState.size < leftPaddle.y + leftPaddle.height
            ) {
                return { ...prevState, vx: Math.abs(prevState.vx), x: newX, y: newY };
            }

            // Bounce off right paddle
            if (
                newX + prevState.size > rightPaddle.x &&
                newY + prevState.size > rightPaddle.y &&
                newY - prevState.size < rightPaddle.y + rightPaddle.height
            ) {
                return { ...prevState, vx: -Math.abs(prevState.vx), x: newX, y: newY };
            }

            // If the ball goes out of left or right side
            if (newX < 0 || newX > CANVAS_WIDTH) {
                // Here you might want to handle point scoring, but for now, I'll reset the ball
                return {
                    ...prevState,
                    x: CANVAS_WIDTH / 2,
                    y: CANVAS_HEIGHT / 2,
                    vx: BALL_SPEED_X,
                    vy: BALL_SPEED_Y
                };
            }

            return { ...prevState, x: newX, y: newY };
        });
    };


    const resetBall = () => {
        ball.x = CANVAS_WIDTH / 2;
        ball.y = CANVAS_HEIGHT / 2;
        ball.vx = BALL_SPEED_X;
        ball.vy = BALL_SPEED_Y;
    };

    const draw = () => {
        if (canvasRef.current) {
            const ctx = canvasRef.current.getContext('2d')!;
            ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

            ctx.fillStyle = 'lightgrey';
            ctx.fillRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
            ctx.fillStyle = 'black';
            ctx.fillRect(leftPaddle.x, leftPaddle.y, leftPaddle.width, leftPaddle.height);
            ctx.fillRect(rightPaddle.x, rightPaddle.y, rightPaddle.width, rightPaddle.height);
            ctx.strokeStyle = 'black';
            ctx.lineWidth = BORDER_THICKNESS;
            ctx.strokeRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
            ctx.beginPath();
            ctx.arc(ball.x, ball.y, ball.size, 0, Math.PI * 2);
            ctx.fill();
        }
    };

    const gameLoop = () => {
        updateBallPosition();
        draw();
        setTimeout(() => {
            animationFrameId.current = requestAnimationFrame(gameLoop);
        }, 1000 / TICKS_PER_SEC);
    }

    const handleKeyDown = (e: KeyboardEvent) => {
        switch(e.key) {
            case 'ArrowUp':
                setRightPaddle(prevState => {
                    const newY = prevState.y - prevState.dy > BORDER_PADDING_SIDE ? prevState.y - prevState.dy : prevState.y;
                    return { ...prevState, y: newY };
                });
                break;
            case 'ArrowDown':
                setRightPaddle(prevState => {
                    const newY = (prevState.y + prevState.height + prevState.dy < CANVAS_HEIGHT - BORDER_PADDING_SIDE) ? prevState.y + prevState.dy : prevState.y;
                    return { ...prevState, y: newY };
                });
                break;
            case 'w':
                setLeftPaddle(prevState => {
                    const newY = prevState.y - prevState.dy > BORDER_PADDING_SIDE ? prevState.y - prevState.dy : prevState.y;
                    return { ...prevState, y: newY };
                });
                break;
            case 's':
                setLeftPaddle(prevState => {
                    const newY = (prevState.y + prevState.height + prevState.dy < CANVAS_HEIGHT - BORDER_PADDING_SIDE) ? prevState.y + prevState.dy : prevState.y;
                    return { ...prevState, y: newY };
                });
                break;
            default:
                break;
        }
    };

    useEffect(() => {
        draw();
        return () => {
            if (animationFrameId.current !== null) {
                cancelAnimationFrame(animationFrameId.current);
            }
        };
    }, []);

    useEffect(() => {
        document.addEventListener('keydown', handleKeyDown);
        return () => {
            document.removeEventListener('keydown', handleKeyDown);
        };
    }, [leftPaddle, rightPaddle]);

    return (
        <div className="game-container">
            <canvas ref={canvasRef} width={CANVAS_WIDTH} height={CANVAS_HEIGHT} />
        </div>
    );
}

export default Game;
