// Navbar.tsx
import React from 'react';
import styled from 'styled-components';
import { Link as RouterLink } from 'react-router-dom';
import logo from './assets/logo.svg';  // Ensure you have the correct path to your logo.

const NavbarContainer = styled.nav`
  position: fixed;
  top: 0;
  width: 100%;
  height: 5%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 2em;
  background-color: rgba(0, 0, 0, 0.8); // You can change this to your theme color.
  z-index: 1000;
`;

const StyledRouterLink = styled(RouterLink)`
  color: white;
  font-family: 'Arial', sans-serif; 
  font-weight: bold;
  font-size: 0.9rem;
  cursor: pointer;
  transition: color 0.2s ease;
  text-decoration: none;
  margin: 0 1em;

  &:hover {
    color: grey;
  }
`;

const Logo = styled.img`
  height: 40px;
  animation: App-logo-spin infinite 20s linear;
  @keyframes App-logo-spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`;

function Navbar() {
  return (
    <NavbarContainer>
      <Logo src={logo} alt="App Logo" />
      <div>
        <StyledRouterLink to="/">Home</StyledRouterLink>
        <StyledRouterLink to="/game">Game</StyledRouterLink>
      </div>
    </NavbarContainer>
  );
}

export default Navbar;
